import { useState } from 'react';
import { StyleSheet, View, TouchableOpacity, Text, StatusBar, SafeAreaView } from 'react-native';
import { Row, Button } from './src/components';

export default function App() {
  const [input, setInput] = useState(' ');
  let operants = [];
  let operators = [];
  let temp = [];

  const closeInput = () => {
    let op = temp.join('');
    operants.push(Number(op))
    temp = [];
  }

  const calculate = () => {
    let res = operants[0];
    for(let i=1; i< operants.length; i++)
    {
      switch(operators[i-1])
      {
        case '/' : res = res / operants[i] ;break;
        case '*' : res = res * operants[i] ;break;
        case '-' : res = res - operants[i] ;break;
        case '+' : res = res + operants[i] ;break;
        default : break;
      }
    };
    setInput(res.toString());
  }

  const setUp = () => {
    [...input].forEach( e => {
      if(e !== '/' &&
         e !== '*' &&
         e !== '-' &&
         e !== '+') 
      {
        temp.push(e);
      }
      else if(e === '/' ||
              e === '*' ||
              e === '-' ||
              e === '+')
      {
        operators.push(e);
        closeInput();
      }
    });
    operators.push('=');
    closeInput();
    calculate();
  }


  const onKey1Push = () => {
    setInput(input + '1');
  }

  const onKey2Push = () => {
    setInput(input + '2');
  }

  const onKey3Push = () => {
    setInput(input + '3');
  }

  const onKey4Push = () => {
    setInput(input + '4');
  }

  const onKey5Push = () => {
    setInput(input + '5');
  }

  const onKey6Push = () => {
    setInput(input + '6');
  }

  const onKey7Push = () => {
    setInput(input + '7');
  }

  const onKey8Push = () => {
    setInput(input + '8');
  }

  const onKey9Push = () => {
    setInput(input + '9');
  }

  const onKey0Push = () => {
    setInput(input + '0');
  }

  const onKeyAcPush = () => {
    setInput(' ');
  }

  const onKeyDividePush = () => {
    setInput(input + '/');
  }

  const onKeyMultiplyPush = () => {
    setInput(input + '*');
  }

  const onKeySubtractPush = () => {
    setInput(input + '-');
  }

  const onKeyAddPush = () => {
    setInput(input + '+');
  }

  const onKeyEqualsPush = () => {
    setInput(input + '=');
    console.log('input is :' + input);
    setUp();
  }

  return (
    <View style={styles.container}>
      <StatusBar barStyle="light-content"/>
      <SafeAreaView>
        <Text style={styles.value}>
          {input}
        </Text>
        <Row>
          <Button
          text="1"
          theme="seconday"
          onPress={onKey1Push}
          />
          <Button
          text="2"
          theme="seconday"
          onPress={onKey2Push}
          />
          <Button
          text="3"
          theme="seconday"
          onPress={onKey3Push}
          />
        </Row>
        <Row>
          <Button
          text="4"
          theme="seconday"
          onPress={onKey4Push}
          />
          <Button
          text="5"
          theme="seconday"
          onPress={onKey5Push}
          />
          <Button
          text="6"
          theme="seconday"
          onPress={onKey6Push}
          />
        </Row>
        <Row>
          <Button
          text="7"
          theme="seconday"
          onPress={onKey7Push}
          />
          <Button
          text="8"
          theme="seconday"
          onPress={onKey8Push}
          />
          <Button
          text="9"
          theme="seconday"
          onPress={onKey9Push}
          />
        </Row>
      </SafeAreaView>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#202020",
    justifyContent: "flex-end"
  },
  value: {
    color: "#fff",
    fontSize: 40,
    textAlign: "right",
    marginRight: 20,
    marginBottom: 10
  }
});
