import { useState } from 'react';
import { StyleSheet, View, Button, Text } from 'react-native';

export default function App() {
  const [input, setInput] = useState(' ');
  const [result, setResult] = useState(0);
  let operants = [];
  let operators = [];
  let temp = [];

  const closeInput = () => {
    let op = temp.join('');
    operants.push(Number(op))
    temp = [];
  }

  const calculate = () => {
    console.log('calculating');
    console.log('operators: ' + operators);
    console.log('operants: ' + operants);
    let res = operants[0];
    for(let i=1; i< operants.length; i++)
    {
      console.log('operators' + operators[i]);
      console.log('operants' + operants[i]);
      console.log('current answer' + res);
      switch(operators[i-1])
      {
        case '/' : res = res / operants[i] ;break;
        case '*' : res = res * operants[i] ;break;
        case '-' : res = res - operants[i] ;break;
        case '+' : res = res + operants[i] ;break;
        default : break;
      }
    };
    setResult(res);
    console.log(res.toString());
    setInput(res.toString());
  }

  const setUp = () => {
    [...input].forEach( e => {
      if(e !== '/' &&
         e !== '*' &&
         e !== '-' &&
         e !== '+') 
      {
        temp.push(e);
      }
      else if(e === '/' ||
              e === '*' ||
              e === '-' ||
              e === '+')
      {
        operators.push(e);
        closeInput();
      }
    });
    operators.push('=');
    closeInput();
    calculate();
    setInput(' ');
  }


  const onKey1Push = () => {
    setInput(input + '1');
  }

  const onKey2Push = () => {
    setInput(input + '2');
  }

  const onKey3Push = () => {
    setInput(input + '3');
  }

  const onKey4Push = () => {
    setInput(input + '4');
  }

  const onKey5Push = () => {
    setInput(input + '5');
  }

  const onKey6Push = () => {
    setInput(input + '6');
  }

  const onKey7Push = () => {
    setInput(input + '7');
  }

  const onKey8Push = () => {
    setInput(input + '8');
  }

  const onKey9Push = () => {
    setInput(input + '9');
  }

  const onKey0Push = () => {
    setInput(input + '0');
  }

  const onKeyAcPush = () => {
    setInput(' ');
  }

  const onKeyDividePush = () => {
    setInput(input + '/');
  }

  const onKeyMultiplyPush = () => {
    setInput(input + '*');
  }

  const onKeySubtractPush = () => {
    setInput(input + '-');
  }

  const onKeyAddPush = () => {
    setInput(input + '+');
  }

  const onKeyEqualsPush = () => {
    setInput(input + '=');
    console.log('input is :' + input);
    setUp();
  }

  return (
    <View style={
      {
        flexDirection: 'column',
        flex: 1,
        alignContent: "center",
        alignItems: "center",
        marginVertical: "auto"
        }}>
      <Text>{input}</Text>
      <View>
        <View style={styles.container_horizontal}>
          <View>
            <View style={styles.buttons_container_horizontal}>
              <Button 
                color={'#99a1ad'}
                style={styles.button}
                title='1'
                onPress={onKey1Push}>
              </Button>      
              <Button 
                color={'#99a1ad'}
                style={styles.button}
                title='2'
                onPress={onKey2Push}>
              </Button>      
              <Button 
                color={'#99a1ad'}
                style={styles.button}
                title='3'
                onPress={onKey3Push}>
              </Button>
            </View>

            <View style={styles.buttons_container_horizontal}>
              <Button 
                color={'#99a1ad'}
                style={styles.button}
                title='4'
                onPress={onKey4Push}>
              </Button>      
              <Button 
                color={'#99a1ad'}
                style={styles.button}
                title='5'
                onPress={onKey5Push}>
              </Button>      
              <Button 
                color={'#99a1ad'}
                style={styles.button}
                title='6'
                onPress={onKey6Push}>
              </Button>
            </View>
            <View style={styles.buttons_container_horizontal}>
              <Button 
                color={'#99a1ad'}
                style={styles.button}
                title='7'
                onPress={onKey7Push}>
              </Button>      
              <Button 
                color={'#99a1ad'}
                style={styles.button}
                title='8'
                onPress={onKey8Push}>
              </Button>      
              <Button 
                color={'#99a1ad'}
                style={styles.button}
                title='9'
                onPress={onKey9Push}>
              </Button>
            </View>
            <View style={styles.buttons_container_horizontal}>
              <Button
              color={'#99a1ad'}
              style={styles.button}
              title='0'
              onPress={onKey0Push}>
              </Button>
              <Button
              color={'#99a1ad'}
              style={styles.button}
              title='AC'
              onPress={onKeyAcPush}>
              </Button>
            </View>
          </View>
          <View>
          <View
          style={styles.buttons_container_vertical}>
            <Button
            color={'#99a1ad'}
            style={styles.button}
            title='/'
            onPress={onKeyDividePush}>
            </Button>
            <Button
            color={'#99a1ad'}
            style={styles.button}
            title='*'
            onPress={onKeyMultiplyPush}>
            </Button>
            <Button
            color={'#99a1ad'}
            style={styles.button}
            title='-'
            onPress={onKeySubtractPush}>
            </Button>
            <Button
            color={'#99a1ad'}
            style={styles.button}
            title='+'
            onPress={onKeyAddPush}>
            </Button>
            <Button
            color={'#99a1ad'}
            style={styles.button}
            title='='
            onPress={onKeyEqualsPush}>
            </Button>
          </View>
        </View>
          </View>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: "row",
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  container_horizontal: {
    flexDirection: "row"
  },
  button: {
    margin: 10,
    alignItems: 'center',
    justifyContent: 'center',
  },
  buttons_container_horizontal: {
    flex: 1,
    flexDirection : "row",
    alignItems: 'center',
    justifyContent: 'center',
  },
  buttons_container_vertical: {
    flex: 1,
    flexDirection : "column",
    alignItems: 'center',
    justifyContent: 'center',
  }
});
