import { useState } from 'react';
import  {StyleSheet, View, Button, Text } from 'react-native';
import { StatusBar } from 'expo-status-bar';

export default function App() {
  const [input, setInput] = useState('');

  const onKey1Push = () => {
    setInput(input + '1');
  }

  const onKey2Push = () => {
    setInput(input + '2');
  }

  const onKey3Push = () => {
    setInput(input + '3');
  }

  const onKey4Push = () => {
    setInput(input + '4');
  }

  const onKey5Push = () => {
    setInput(input + '5');
  }

  const onKey6Push = () => {
    setInput(input + '6');
  }

  const onKey7Push = () => {
    setInput(input + '7');
  }

  const onKey8Push = () => {
    setInput(input + '8');
  }

  const onKey9Push = () => {
    setInput(input + '9');
  }

  const onKey0Push = () => {
    setInput(input + '0');
  }

  const onKeyAcPush = () => {
    setInput('');
  }

  const onKeyDividePush = () => {
    setInput(input + ' / ')
  }

  const onKeyMultiplyPush = () => {
    setInput(input + ' * ')
  }

  const onKeySubtractPush = () => {
    setInput(input + ' - ')
  }

  const onKeyEqualsPush = () => {
    setInput(input + ' = ')
  }

  const onKeyPlusPush = () => {
    setInput(input + ' + ')
  }



  return (
    <View style={styles.container}>
      <Text>{input}</Text>
      <View style={styles.container_horizontal}>
        <View>
          <View style={styles.buttons_container_horizontal}>
          <Button 
            color={'#99a1ad'}
            style={styles.button}
            title='1'
            onPress={onKey1Push}>
          </Button>      <Button 
            color={'#99a1ad'}
            style={styles.button}
            title='2'
            onPress={onKey2Push}>
          </Button>      <Button 
            color={'#99a1ad'}
            style={styles.button}
            title='3'
            onPress={onKey3Push}>
          </Button>
          </View>
          <View style={styles.buttons_container_horizontal}>
          <Button 
            color={'#99a1ad'}
            style={styles.button}
            title='4'
            onPress={onKey4Push}>
          </Button>      <Button 
            color={'#99a1ad'}
            style={styles.button}
            title='5'
            onPress={onKey5Push}>
          </Button>      <Button 
            color={'#99a1ad'}
            style={styles.button}
            title='6'
            onPress={onKey6Push}>
          </Button>
          </View>
          <View style={styles.buttons_container_horizontal}>
          <Button 
            color={'#99a1ad'}
            style={styles.button}
            title='7'
            onPress={onKey7Push}>
          </Button>      <Button 
            color={'#99a1ad'}
            style={styles.button}
            title='8'
            onPress={onKey8Push}>
          </Button>      <Button 
            color={'#99a1ad'}
            style={styles.button}
            title='9'
            onPress={onKey9Push}>
          </Button>
          </View>
          <View style={styles.buttons_container_horizontal}>
            <Button
            color={'#99a1ad'}
            style={styles.button}
            title='0'
            onPress={onKey0Push}>
            </Button>
            <Button
            color={'#99a1ad'}
            style={styles.button}
            title='AC'
            onPress={onKeyAcPush}>
            </Button>
          </View>
        </View>
        <View
        style={styles.buttons_container_vertical}>
          <Button
          color={'#99a1ad'}
          style={styles.button}
          title={'/'}>
          </Button>
          <Button
          color={'#99a1ad'}
          style={styles.button}
          title={'*'}>
          </Button>
          <Button
          color={'#99a1ad'}
          style={styles.button}
          title={'-'}>
          </Button>
          <Button
          color={'#99a1ad'}
          style={styles.button}
          title={'+'}>
          </Button>
          <Button
          color={'#99a1ad'}
          style={styles.button}
          title={'*'}>
          </Button>
        </View>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: "row",
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  container_horizontal: {
    flexDirection: "row"
  },
  button: {
    margin: 10,
    alignItems: 'center',
    justifyContent: 'center',
  },
  buttons_container_horizontal: {
    flex: 1,
    flexDirection : "row",
    alignItems: 'center',
    justifyContent: 'center',
  },
  buttons_container_vertical: {
    flex: 1,
    flexDirection : "column",
    alignItems: 'center',
    justifyContent: 'center',
  }
});
